    private void salvarButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_salvarButtonActionPerformed
        
        // Declarando e instanciando um novo objeto Cadastro
        Cadastro cadastro = new Cadastro();
        
        // Atribuindo valores dos campos do formulÃ¡rio
        // aos atributos da classe Cadastro
        cadastro.setNome(nomeCampo.getText());
        cadastro.setCidade(cidadeCampo.getText());
        cadastro.setEmail(emailCampo.getText());
        cadastro.setTelefone(telefoneCampo.getText());
        cadastro.setEndereco(enderecoCampo.getText());
        // Casting para gravar o estado selecionado
        cadastro.setEstado((String)estadoCampo.getSelectedItem());
        cadastro.setMotivo(motivoCampo.getText());
        
        // Verificando o Sexo selecionado
        if(masculinoCampo.isSelected()) {
            cadastro.setSexo("M");
        } else {
            cadastro.setSexo("F");
        }
        
        // Verificando os idiomas selecionados
        if(alemaoCampo.isSelected()) {
            cadastro.setAlemao(true);
        }
        if(italianoCampo.isSelected()) {
            cadastro.setItaliano(true);
        }
        if(francesCampo.isSelected()) {
            cadastro.setFrances(true);
        }
        if(inglesCampo.isSelected()) {
            cadastro.setIngles(true);
        }
        if(espanholCampo.isSelected()) {
            cadastro.setEspanhol(true);
        }
        if(japonesCampo.isSelected()) {
            cadastro.setJapones(true);
        }
        
        // Limpando os campos do formulÃ¡rio
        limpar();
        
        // Exibindo uma caixa de diÃ¡logo para confirmaÃ§Ã£o usando JOptionPane
        JOptionPane.showMessageDialog(null,"O cadastro foi salvo!");

    }//GEN-LAST:event_salvarButtonActionPerformed

    public void limpar() {
        // Inicializando os campos do formulÃ¡rio
        nomeCampo.setText("");
        enderecoCampo.setText("");
        telefoneCampo.setText("");
        emailCampo.setText("");
        cidadeCampo.setText("");
        motivoCampo.setText("");
        espanholCampo.setText("");
        alemaoCampo.setSelected(false);
        inglesCampo.setSelected(false);
        italianoCampo.setSelected(false);
        francesCampo.setSelected(false);
        japonesCampo.setSelected(false);
        buttonGroup1.clearSelection();
        estadoCampo.setSelectedIndex(-1);
    }
    
